import react, { useState, useRef } from 'react';
import './App.css';
import WordsList from './WordsList';
import AddWords from './AddWords';

function App() {

  const [wordsList, setWordsList] = useState([
    {
      id: 1,
      english: 'english',
      korean: '영어'
    },
    {
      id: 2,
      english: 'korean',
      korean: '한글'
    },
  ]);

  const [addWords, setAddWords] = useState({
      english: '',
      korean: ''
  });

  const nextId = useRef(3);

  const onChange = e => {
    console.log(e.target.value);
  }

  const onSave = () => {
    alert('등록되었습니다!');
  }

  return (
    <div className="app">
      <div className="container">
        <input type="text" name="english" placeholder="영단어" onChange={onChange}/>
        <input type="text" name="korean" placeholder="뜻" onChange={onChange}/>
        <button onClick={onSave}>등록</button>
        <button>시험보기</button>
        <WordsList wordsList={wordsList}/>
      </div>
    </div>
  );
}

export default App;
